import React from 'react'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'

const ProfileItem = ({profile: {user:{_id, name, avatar} , status , location, skills, company}}) => {
    return (
        <div className = "profile bg-light">
            <img src={avatar} className = "round-img" />
            <div>
            <h2>{name}</h2>
    <p>{status} {company && <span>at {company}</span>}</p>
    <p className = "my-1"> {location && <span>at {location}</span>}</p>
    <Link className = {`/profile/${_id}`} className = "btn btn-primary">
        View Profile
    </Link>
    </div>
    <ul>
        {skills.slice(0,4).map((skill , index) => (
            <li key = {index} className = 'text-primary'>
                <i className = "fa fa-check"/> {skill}
            </li>
        ))}
    </ul>
    </div>
    )
}

ProfileItem.propTypes = {

}

export default ProfileItem
