import {
    GET_POSTS,
    POST_ERROR,
    UPDATE_POST
} from '../actions/types'

const initialState = {
    posts: [],
    post: null,
    loading: false,
    error: {}
}

export default function(state=initialState , action){
    const {type , payload} = action

    switch (type) {
        case GET_POSTS:
            return {
                ...state,
                posts: payload,
                loading: false
            }
        case UPDATE_POST:
            return {
                ...state,
                posts: state.posts.map(post => post._id === payload.id ? { ...post , likes: payload.likes } : post),
                loading: false
            }
        case POST_ERROR:
            return {
                ...state,
                error: payload,
                loading: false
            }
        default:
            return state;
    }

}
