import {
    GET_POSTS,
    POST_ERROR,
    UPDATE_LIKES,
    DELETE_POST,
    ADD_POST,
    GET_POST,
    ADD_COMMENT,
    DELETE_COMMENT
} from '../actions/types'
import { setAlert } from '../actions/alert'

const initialState = {
    posts: [],
    post: null,
    loading: false,
    error: {}
}

export default function(state=initialState , action){
    const {type , payload} = action

    switch (type) {
        case GET_POSTS:
            return {
                ...state,
                posts: payload,
                loading: false
            }
        case GET_POST:
            return {
                ...state,
                post: payload,
                loading: false
            }
        case UPDATE_LIKES:
            return {
                ...state,
                posts: state.posts.map(post => post._id === payload.postID ? 
                    { ...post , likes: payload.likes } : post),
                loading: false
            }
        case ADD_POST:
            return {
                ...state,
                posts: [payload , ...state.posts],
                loading: false
            }
        case ADD_COMMENT:
            return {
                ...state,
                post: {...state.post , comments: payload},
                loading: false
            }
        case DELETE_POST: 
            return {
                ...state,
                // remove the post from posts array
                posts: state.posts.filter(post => post._id !== payload) ,
                loading: false
            }
        case DELETE_COMMENT:
            return {
                ...state,
                post: {
                    ...state.post,
                    // remove comment from comments array
                    comments: state.post.comments.filter(comm => comm._id !== payload),
                    loading: false
                }
            }
        case POST_ERROR:
            return {
                ...state,
                error: payload,
                loading: false
            }
        default:
            return state;
    }

}
