import { SET_ALERT , REMOVE_ALERT } from './types';
import { v4 as uuidv4 } from 'uuid';
export const setAlert = (msg , alertType , timeout = 5000) => {
    const id = uuidv4();
    if(alertType === 'danger'){
        return {
            type: SET_ALERT,
            payload: {msg , alertType , id}
        }
    }

